const ll M = 2;
const ll K[M] = {1000003, 1791791};
const ll rK[M] = {rev(K[0]), rev(K[1])};
map<ll, ll> deg[M];

vector<ll> ad(vector<ll> a)
{
    for (int i = 0; i < M; i++)
    {
        a[i]++;
        if (a[i] >= MOD) a[i] -= MOD;
    }
    return a;
}

vector<ll> mi(vector<ll> a)
{
    for (int i = 0; i < M; i++)
    {
        a[i]--;
        if (a[i]  < 0) a[i] += MOD;
    }
    return a;
}

vector<ll> mu(vector<ll> a)
{
    for (int i = 0; i < M; i++)
    {
        a[i] = a[i] * K[i] % MOD;
    }
    return a;
}

vector<ll> di(vector<ll> a)
{
    for (int i = 0; i < M; i++)
    {
        a[i] = a[i] * rK[i] % MOD;
    }
    return a;
}

signed main()
{
    for (int i = 0; i < M; i++)
    {
        deg[i][0] = 1;
        for (int j = 1; j < N; j++) deg[i][j] = deg[i][j - 1] * K[i] % MOD;
        for (int j = 1; j < N; j++) deg[i][-j] = deg[i][-j + 1] * rK[i] % MOD;
    }
    srand(time(NULL));
    #ifdef ONLINE_JUDGE
        ios_base::sync_with_stdio(false);
        cin.tie(0);
        cout.tie(0);
    #endif // ONLINE_JUDGE
    cin >> n >> s;
    vector<ll> a(M);
    for (int i = n - 1; i >= 0; i--)
    {
        if (s[i] == '+') a = mi(a);
        if (s[i] == '-') a = ad(a);
        if (s[i] == '>') a = mu(a);
        if (s[i] == '<') a = di(a);
    }
    map<vector<ll>, int> cnt;
    ll ans = 0;
    ll cur_move = 0;
    vector<ll> cur(M, 0);
    cnt[cur]++;
    for (int i = n - 1; i >= 0; i--)
    {
        if (s[i] == '+')
        {
            for (int j = 0; j < M; j++) cur[j] = (cur[j] + deg[j][cur_move]) % MOD;
        }
        if (s[i] == '-')
        {
            for (int j = 0; j < M; j++) cur[j] = (cur[j] + MOD - deg[j][cur_move]) % MOD;
        }
        if (s[i] == '<')
        {
            cur_move++;
        }
        if (s[i] == '>')
        {
            cur_move--;
        }
        vector<ll> ask = cur;
        //for (int j = 0; j < M; j++) cout << cur[j] << " "; cout << "\n";
        for (int j = 0; j < M; j++) ask[j] = (ask[j] + a[j] * deg[j][cur_move]) % MOD;
        //for (int j = 0; j < M; j++) cout << ask[j] << " "; cout << "\n\n";
        ans += cnt[ask];
        cnt[cur]++;
    }
    cout << ans;
}

