using namespace std;

typedef long long ll;

const int mod = 1e9 + 7;
const int MN = 250010;
const int P = 1e6 + 7;

int exp(int x, int n) {
    int ret = 1;
    while(n) {
        if(n & 1) ret = 1LL * ret * x % mod;
        x = 1LL * x * x % mod;
        n >>= 1;
    }
    return ret;
}
int inv(int x) {
    return exp(x, mod - 2);
}
int po[MN << 1];

int N;
char S[MN];
int A[MN << 1];
unordered_map<int, int> cnt;

int main() {
    po[0] = 1;
    for(int i = 1; i < (MN << 1); i++) {
        po[i] = 1LL * po[i - 1] * P % mod;
    }

    scanf("%d", &N);

    scanf("\n");
    for(int i = 0; i < N; i++) {
        scanf("%c", &S[i]);
    }

    int pos = MN;
    for(int i = 0; i < N; i++) {
        if(S[i] == '+') A[pos]++;
        if(S[i] == '-') A[pos]--;
        if(S[i] == '<') pos--;
        if(S[i] == '>') pos++;
    }

    int hval = 0;
    for(int i = 0; i < (MN << 1); i++) {
        hval += 1LL * (A[i] + mod) % mod * po[i] % mod;
        hval %= mod;
    }

    int a = 1, b = 0;
    ll ans = 0;
    for(int i = N - 1; i >= 0; i--) {

        cnt[ 1LL * (mod - b) * inv(a) % mod ]++;

        if(S[i] == '+') {
            b += po[MN];
            b %= mod;
        }
        if(S[i] == '-') {
            b += mod - po[MN];
            b %= mod;
        }
        if(S[i] == '<') {
            a = 1LL * a * inv(P) % mod;
            b = 1LL * b * inv(P) % mod;
        }
        if(S[i] == '>') {
            a = 1LL * a * P % mod;
            b = 1LL * b * P % mod;
        }

        ans += cnt[ 1LL * (hval + mod - b) % mod * inv(a) % mod ];
    }

    printf("%lld", ans);
}

