}
constexpr int m0 = 1192230913,b0 = 233333;
constexpr int m1 = 1085276161,b1 = 2333333;
struct Int{
	int a0,a1;
	inline Int(int x0 = 0,int x1 = 0){ a0 = x0,a1 = x1; }
	inline void add(){ reduce(a0 += b0 - m0,m0); reduce(a1 += b1 - m1,m1); }
	inline void dec(){ reduce(a0 -= b0,m0); reduce(a1 -= b1,m1); }
	inline void mul(int cnt){ a0 = pow(b0,cnt,m0,a0); a1 = pow(b1,cnt,m1,a1); }
	inline bool operator < (const Int & b) const { return * (ll*) this < * (ll*) & b; }
} a[maxn], b[maxn];

