typedef unsigned long long ull;
const ull H=300031;
const int SZ=300000;                // need SZ > n
//const ull H=10;
//const int SZ=6;
const int INF=0x3f3f3f3f;

ull h0,h1,h2;

int n;
char s[SZ];

ull hashes[SZ*2+5];
ull shash[SZ];
int p;

// if the interval [i,zerojump[i]) is eaten, then result is 0 everywhere
// also zerojump[i]>=i and zerojump is minimal
std::map<ull,int> m;
int zerojump[SZ];
int extraans[SZ];

std::map<ull,int> m2;       // answer search
std::set<int> s2[SZ];
std::set<int>::iterator it;

long long ans=0;

int main()
{
    #ifdef NOT_DMOJ
    freopen("data.txt","r",stdin);
    #endif // NOT_DMOJ
    hashes[0]=1;
    for (int i=1; i<=SZ*2; i++) {
        hashes[i]=hashes[i-1]*H;
    }
    scanf("%d",&n);
    scanf(" %s",s);
    // get zerojump
    int p=0;
    h1=h2=0;
    memset(zerojump,0x3f,sizeof zerojump);
    m2[0]=0;
    s2[0].insert(0);
    m[0]=0;
    for (int i=0; i<n; i++) {
        // do the action
        if (s[i]=='<') p--;
        else if (s[i]=='>') p++;
        else if (s[i]=='+') {
            h1+=hashes[p+SZ];
        }
        else {
            h1-=hashes[p+SZ];
        }
        shash[i+1]=h1;
        h2=h1*hashes[SZ];
        // zerojump logic
        if (m.count(h1)) {
            zerojump[m[h1]]=i+1;
            m[h1]=i+1;
            s2[m2[h2]].insert(i+1);
        }
        else {
            m[h1]=i+1;
            m2[h2]=i+1;
            s2[i+1].insert(i+1);
        }
        //printf("s2 %d insert %d\n",m2[h2],i+1);
    }
    // fill in extraans
    extraans[n]=1;
    for (int i=n-1; i>=0; i--) {
        if (zerojump[i]==INF) extraans[i]=1;
        else extraans[i]=1+extraans[zerojump[i]];
    }
    // get answer
    // note: shash[n] is the final hash value wanted
    for (int i=0; i<SZ; i++) {
        if (s2[i].empty()) continue;
        s2[i].insert(INF);
    }
    p=SZ;
    //for (auto aa:m2) printf("h2      %lld %d\n",aa);
    //for (int i=0; i<=n; i++) printf("sh %d %lld\n",i,shash[i]);
    for (int i=0; i<n; i++) {
        h2=(shash[n]*hashes[p]+shash[i]*hashes[SZ]);
        //printf("go find %lld\n",h2);
        if (m2.count(h2)) {
            it=s2[m2[h2]].lower_bound(i+1);
            //printf("index %d got %d\n",i,*it);
            if ((*it)!=INF) {
                //printf("extraans %d\n",extraans[*it]);
                ans+=extraans[*it];
            }
        }
        if (s[i]=='<') p--;
        else if (s[i]=='>') p++;
    }
    printf("%lld\n",ans);
}

