valarray<long long int> MOD1;
valarray<long long int> MOD2;
vector<valarray<long long int> > p2;
vector<valarray<long long int> > vv;
vector<int> ps;
map<pair<long long int,long long int>, int> mp;
long long int ppow(long long int i, long long int j, long long int MOD) {
	long long int ret = 1;
	while (j) {
		if (j & 1LL)ret *= i;
		if (ret >= MOD)ret %= MOD;
		j >>= 1LL;
	}
	return ret;
}

valarray<long long int> calc(int dif) {
	valarray<long long int> ret;
	ret.resize(2, 0);
	for (int j = 0; j < 2; j++) {
		if (dif < 0) {
			ret[j]=ppow(ppow(MOD1[j], abs(dif),MOD2[j]), MOD2[j] - 2,MOD2[j]);
		}
		else {
			ret[j] = ppow(MOD1[j], abs(dif), MOD2[j]);
		}
	}
	return ret;
}

int main() {
	MOD1.resize(2);
	MOD2.resize(2);
	p2.push_back(valarray<long long int>());
	p2.back().resize(2);
	p2.back()[0] = p2.back()[1] = 1;
	MOD2[0] = 1000000007;
	MOD1[0] = 998244353;
	MOD1[1] = 555522277;
	MOD2[1] = 895612782;
	p2[0] %= MOD2;
	for (int i = 1; i < MAX; i++) {
