	for (int i = 0; i < s.size(); i++) {
		if (s[i] == '<') {
			pos--;
		}
		if (s[i] == '>') {
			pos++;
		}
		if (s[i] == '+') {
			hs += p2[pos];
		}
		if (s[i] == '-') {
			hs -= p2[pos];
			hs += MOD2;
		}
		hs %= MOD2;
		vv.push_back(hs);
		ps.push_back(pos);
	}
	long long int way = 0;
	for (int i = vv.size() - 1; i >= 0; i--) {
		valarray<long long int> want = vv[i] + vv.back()*calc(-MAX/2+ps[i]);
		want = want%MOD2;
		way += mp[make_pair(want[0], want[1])];
		//cerr << "dbg " << way << endl;
