const int maxN = 250011;
const ll mod = 1000000007;
const ll base = 2 * 250013 + 7;

ll poww(ll a, ll b) {
    ll ans = 1;
    while (b > 0) {
        if (b & 1) ans = (ans * a) % mod;
        b >>= 1;
        a = (a * a) % mod;
    }
    return ans;
}

struct smart_map {
    map<ll, ll> M;
    ll bonus;

    smart_map() {
        M.clear();
        bonus = 1;
    }

    void operator*=(ll v) {
        bonus = (bonus * v) % mod;
    }

    void add(ll x) {
        x = (x * poww(bonus, mod - 2)) % mod;
        M[x]++;
    }

    ll query(ll x) {
        x = (x * poww(bonus, mod - 2)) % mod;
        return M[x];
    }
};

ll n, i, act, full;
