        if (s[i] == '<') act = (act * base) % mod, data *= base;
        if (s[i] == '>') act = (act * inv_base) % mod, data *= inv_base;

        ll src = (mod + act - full) % mod;
        ans += data.query(src);

        data.add(act);
    }

    printf("%lld", ans);

    return 0;
}

