struct Op { int a[NHASH],b[NHASH]; };
int pw(int x,int n) { int ret=1; while(true) { if(n&1) ret=(ll)ret*x%HMOD; if((n>>=1)==0) return ret; x=(ll)x*x%HMOD; } }

Op combine(const Op &p,const Op &q) { Op ret; REP(i,NHASH) { ret.a[i]=(ll)p.a[i]*q.a[i]%HMOD; ret.b[i]=((ll)p.a[i]*q.b[i]+p.b[i])%HMOD; } return ret; }
void eval(const Op &p,const int q[NHASH],int ret[NHASH]) { REP(i,NHASH) ret[i]=((ll)p.a[i]*q[i]+p.b[i])%HMOD; }

int n; char s[MAXN+1];

Op opid;
Op opgt,iopgt;
Op oplt,ioplt;
Op oppl,ioppl;
Op opmn,iopmn;
Op f[MAXN];
Op g[MAXN];

Op ff[MAXN+1]; // ff[i] is composition of f for suffix starting at i
Op gg[MAXN+1]; // gg[i] is composition of g for suffix starting at i

int id[NHASH];
int fx[NHASH];
int gx[MAXN+1][NHASH];
int hx[MAXN+1][NHASH];

map<vector<int>,int> mp;

void run() {
	scanf("%d%s",&n,s);

	REP(i,n) switch(s[i]) {
		case '>': f[i]=opgt,g[i]=iopgt; break;
		case '<': f[i]=oplt,g[i]=ioplt; break;
		case '+': f[i]=oppl,g[i]=ioppl; break;
		case '-': f[i]=opmn,g[i]=iopmn; break;
		default: assert(false);
	}

	ff[n]=opid; for(int i=n-1;i>=0;--i) ff[i]=combine(f[i],ff[i+1]);
	gg[n]=opid; for(int i=n-1;i>=0;--i) gg[i]=combine(gg[i+1],g[i]);

	memset(id,0,sizeof(id));
	eval(ff[0],id,fx);
	REPE(i,n) eval(gg[i],id,gx[i]);
	REPE(i,n) eval(gg[i],fx,hx[i]);

	//printf("fx:"); printf(" %d\n",fx[0]);
	//printf("gx:"); REPE(i,n) printf(" %10d",gx[i][0]); puts("");
	//printf("hx:"); REPE(i,n) printf(" %10d",hx[i][0]); puts("");

	ll ret=0;
	for(int i=n;i>=0;--i) {
		vector<int> vg(NHASH),vh(NHASH); REP(j,NHASH) vg[j]=gx[i][j],vh[j]=hx[i][j];
		ret+=mp[vh]; ++mp[vg];
	}
	printf("%lld\n",ret);
}

void init() {
	REP(i,NHASH) { int x=0; REP(j,100) x=((ll)x*1000+rand()%1000)%HMOD; HMLT[i]=x; HINV[i]=pw(x,HMOD-2); }
	//printf("HMLT:"); REP(i,NHASH) printf(" %d",HMLT[i]); puts("");
	REP(i,NHASH) {
		opid.a[i]=1,opid.b[i]=0;
		opgt.a[i]=HMLT[i],opgt.b[i]=0;
		oplt.a[i]=HINV[i],oplt.b[i]=0;
		oppl.a[i]=1,oppl.b[i]=1;
		opmn.a[i]=1,opmn.b[i]=HMOD-1;
		iopgt=oplt,ioplt=opgt,ioppl=opmn,iopmn=oppl;
	}
}

int main() {
	init();
	run();
	return 0;
}

