const int X = 229;

struct hasher {
    vector<int> pw;
    int M;
    int val;

    int add(int a, int b) {
        return (a + b) % M;
    }

    int sub(int a, int b) {
        return (a - b + M) % M;
    }

    int mul(int a, int b) {
        return (int)(((ll)a * b) % M + M) % M;
    }

    int bp(int a, int b) {
        int r = 1;
        while(b) {
            if(b & 1) {
                r = mul(r, a);
            }
            a = mul(a, a);
            b >>= 1;
        }
        return r;
    }

    int div(int a, int b) {
        return mul(a, bp(b, M - 2));
    }

    hasher(int p, int m, int n) : M(m), val(0) {
        pw.resize(n);
        pw[0] = 1;
        for(int i = 1; i < n; ++i) {
            pw[i] = mul(pw[i - 1], p);
        }
    }

    void ins(int p, int x) {
        val = add(val, mul(pw[p], x));
    }

    void ers(int p, int x) {
        val = sub(val, mul(pw[p], x));
    }
};

inline void _main_function()
{
    int n;
    cin >> n;

    string s;
    cin >> s;

    vector<int> a(2 * n + 1);
    int p = n;

    hasher h(1000 * 1000 + 3, 1000 * 1000 * 1000 + 7, sz(a));
    hasher g(1000 * 1000 + 33, 1000 * 1000 * 1000 + 9, sz(a));

    vector<int> ah = {0}, ag = {0};

    for(int i = 0; i < n; ++i) {
        if(s[i] == '<') {
            --p;
        } else if(s[i] == '>') {
            ++p;
        } else {
            h.ers(p, a[p]);
            g.ers(p, a[p]);
            if(s[i] == '+') {
                ++a[p];
            } else if(s[i] == '-') {
                --a[p];
            } else {
                assert(false);
            }
            h.ins(p, a[p]);
            g.ins(p, a[p]);
        }

        ah.push_back(h.val);
        ag.push_back(g.val);
    }

    ll sum = 0;
    map<pair<int, int>, int> cnt;
    int sh = 0;

    for(int i = 0; i <= n; ++i) {
        sum += cnt[{ah[i], ag[i]}];

        if(i && s[i - 1] == '>') {
            ++sh;
        } else if(i && s[i - 1] == '<') {
            --sh;
        }

        if(sh >= 0) {
            int x = h.add(h.mul(ah.back(), h.pw[sh]), ah[i]);
            int y = g.add(g.mul(ag.back(), g.pw[sh]), ag[i]);
            ++cnt[{x, y}];
        } else {
            int x = h.add(h.div(ah.back(), h.pw[-sh]), ah[i]);
            int y = g.add(g.div(ag.back(), g.pw[-sh]), ag[i]);
            ++cnt[{x, y}];
        }
    }

    cout << sum << endl;
}

