	return mod - iv(mod % i,mod) * (mod / i) % mod;
}

ll pw(ll a, ll mod) {
	if (a < 0) { return iv(pw(-a, mod), mod); }

	if (a == 0) { return 1; }
	ll c = pw(a/2, mod);
	c = c * c % mod;
	if (a % 2 == 1) { c = c * pr % mod; }
	return c;
}

