class combination {
    // factorial
    public: std::vector<mint> fact;
    public: std::vector<mint> inv;

    combination(int n) {
      fact.resize(n + 1);
      inv.resize(n + 1);
      fact[0] = 1;
      for (int i = 1; i <= n; i++) {
        fact[i] = fact[i - 1] * i;
      }
      inv[n] = mint(1) / fact[n];
      for (int i = n - 1; i >= 0; i--) {
        inv[i] = inv[i + 1] * (i + 1);
      }
    }

    // nCr
    public: mint get(int n, int r) {
      if (n < r || n < 0 || r < 0) return 0;
      return fact[n]*inv[r]*inv[n-r];
    }
    // nPr
    public: mint p(int n, int r) {
      if (n < r || n < 0) return 0;
      return fact[n]*inv[n-r];
    }
};


class FEatingSymbolsHard {
public:
    void solve(istream& cin, ostream& cout) {
      SPEED;
      int n; cin >> n;
      string s; cin >> s;
      vector<mint> base = {11, 13, 17, 1007, 1009, 10007};
      vector<mint> invBase(6);
      rep(i, 6) {
        invBase[i] = mint(1)/base[i];
      }
      vector<int> pos(n+1);
      vector<vector<mint>> vec(6, vector<mint>(n+1));

      rep(i, n) {
        pos[i+1] = pos[i];
        rep(k, 6) {
          vec[k][i+1] = vec[k][i];
        }
        if (s[i] == '+') {
          rep(k, 6) {
            if (pos[i] < 0) {
              vec[k][i+1] += mod_pow(invBase[k], -pos[i]);
            } else {
              vec[k][i+1] += mod_pow(base[k], pos[i]);
            }
          }
        } else if (s[i] == '-') {
          rep(k, 6) {
            if (pos[i] < 0) {
              vec[k][i+1] -= mod_pow(invBase[k], -pos[i]);
            } else {
              vec[k][i+1] -= mod_pow(base[k], pos[i]);
            }
          }
        } else if (s[i] == '<') {
          pos[i+1]--;
        } else {
          pos[i+1]++;
        }
      }

      map<mint, ll> mp[6];
      ll ans = 0;
      for(int i=n; i>=0; i--) {
        map<int, int> cnt;
        rep(k, 6) {
          mint r;
          if (pos[i] < 0) {
            r = mod_pow(invBase[k], -pos[i]);
          } else {
            r = mod_pow(base[k], pos[i]);
          }
          auto q = vec[k][i] + vec[k][n] * r;
          if (mp[k].find(q) == mp[k].end()) {
            cnt[0]++;
          } else {
            cnt[mp[k][q]]++;
          }
        }
        for(auto e: cnt) {
          if (e.second >= 4) {
            ans += e.first;
          }
        }
        rep(k, 6) {
          mp[k][vec[k][i]]++;
        }
      }

      cout << ans << endl;

    }
};

