typedef long long ll;

const int N=250010,MOD=1000000007,g=100000001;

ll inv(int x){return (x==1 || x==-1)?x:(-(MOD/x)*inv(MOD%x)%MOD);}

int t0[N*2],*pow;

void initialize()
{
	pow=t0+N;

	pow[0]=1;
	for(int i=1;i<N;i++)
		pow[i]=(ll)pow[i-1]*g%MOD;

	int ig=(inv(g)+MOD)%MOD;
	for(int i=0;i<N;i++)
		pow[-i-1]=(ll)pow[-i]*ig%MOD;
}

char s[N];
int ind[N],val[N],sum[N];
int n;

ll ans;

void prework()
{
	int p=0;
	for(int i=1;i<=n;i++)
	{
		ind[i]=p,val[i]=0;

		if(s[i]=='<')p--;
		else if(s[i]=='>')p++;
		else if(s[i]=='+')val[i]=pow[p];
		else val[i]=-pow[p];
	}

	for(int i=1;i<=n;i++)
		sum[i]=((sum[i-1]+val[i])%MOD+MOD)%MOD;
}

std::map<int,int> G;

void solve()
{
	ans=0;
	for(int i=n,x,y;i;i--)
	{
		G[sum[i]]++;

		x=((ll)sum[n]*pow[ind[i]]%MOD+MOD)%MOD;
		y=((x+sum[i-1])%MOD+MOD)%MOD;

		ans+=G[y];
	}
}

int main()
{
	initialize();

	scanf("%d",&n);
	scanf("%s",s+1);

	prework();
	solve();

	printf("%lld\n",ans);

	return 0;
}

