typedef long long ll;
const ll N=25e4,mo=23333333333333333,M=2e7+3;
ll pw[N],ny[N];
ll h[M],g[M],n,S,v[N],ans;
char s[N];
ll mul(ll a,ll b){
	ll c=(long double)a*b/mo,z=a*b-c*mo;
	return (z<0)?z+mo:(z-mo*(z>=mo));
}
ll power(ll a,ll b=mo-2){
	ll ans=1;
	while (b){
		if (b&1)ans=mul(ans,a);
		a=mul(a,a);
		b>>=1;
	}return ans;
}
ll base(int p){
	return p<0?ny[-p]:pw[p];
}
int gethash(ll x){
	int a=x%M;
	while (~h[a]&&h[a]!=x)a=(a+1)%M;
	return a;
}
int main(){
	memset(h,255,sizeof h);
	cin>>n;
	pw[0]=ny[0]=1;
	pw[1]=(1e7+19),ny[1]=power(pw[1]);
	for (int i=2;i<=n;i++)
		pw[i]=mul(pw[i-1],pw[1]),
		ny[i]=mul(ny[i-1],ny[1]);
