int64 exgcd(int64 a,int64 b,int64 &c,int64 &d)
{
	if(b == 0) {
		c = 1, d = 0;
		return a;
	}
	int64 k = exgcd(b,a%b,d,c);
	d -= c * (a / b);
	return k;
}
using hash = std::pair<int,int>;
hash operator +(const hash &a, const hash &b) {
	return hash(((int64)a.first+b.first)%mod1,((int64)a.second+b.second)%mod2);
}
hash operator *(const hash &a, const hash &b) {
	return hash((int64)a.first*b.first%mod1,(int64)a.second*b.second%mod2);
}
hash operator -(const hash &a, const hash &b) {
	int v1 = a.first-b.first, v2 = a.second-b.second;
	if(v1<0) v1+=mod1; if(v2<0) v2+=mod2;
	return hash(v1,v2);
}
hash inv(const hash &x) {
	int64 v1,v2,v3,v4;
	assert(exgcd(mod1,x.first,v1,v2) == 1);
	assert(exgcd(mod2,x.second,v3,v4) == 1);
	return hash(v2<0?v2+mod1:v2,v4<0?v4+mod2:v4);
}
hash operator /(const hash &a, const hash &b) {
	return a*inv(b);
}
