	for (int i = 0; i < K; i++) {
		pw[M - 1].x[i] = bin_pow(PP, MODS[i] - 2, MODS[i]);
	}
	for (int i = M + 2; i < N; i++)
		pw[i] = pw[i - 1] * pw[M + 1];
	for (int i = M - 2; i >= 0; i--)
		pw[i] = pw[i + 1] * pw[M - 1];
}

