		x.f += mod1;
	(x.s += y.s) %= mod2;
	if (x.s < 0)
		x.s += mod2;
}

pii mult(pii x, pii y) {
	return mp(x.f * 1ll * y.f % mod1, x.s * 1ll * y.s % mod2);
}

