int bp(int x, int mod) {
	int pw = mod - 2;
	int ans = 1;
	while (pw) {
		if (pw & 1) 
			ans = (ans * 1ll * x) % mod;
		x = (x * 1ll * x) % mod;
		pw /= 2;
	}
	return ans;
}

pii inv(pii x) {
	return mp(bp(x.f, mod1), bp(x.s, mod2));
}

