std::vector<int> primes{(int)1e9 + 7, (int)1e8 + 7, 233333, (int)1e6 + 7, 13};

int inv(int x) {
  return x == 1 ? 1 : (MOD - MOD / x) * 1LL * inv(MOD % x) % MOD;
}

LL solve() {
  int seed = primes[rand() % primes.size()];
  int inv_seed = inv(seed);
  int a = 1, b = 0;
  LL ret = 0;
  std::map<int, int> map;
  map[0] = 1;
  int c = 0;
  for (int i = n; i >= 1; -- i) {
    char ch = s[i - 1];
    if (ch == '+')
      c = (c + 1) % MOD;
    else if (ch == '-')
      c = (c + MOD - 1) % MOD;
    else if (ch == '>')
      c = c * 1LL * seed % MOD;
    else
      c = c * 1LL * inv_seed % MOD;
  }
  for (int i = n; i >= 1; -- i) {
    char ch = s[i - 1];
    if (ch == '-')
      b = (b + a) % MOD;
    else if (ch == '+')
      b = (b + MOD - a) % MOD;
    else if (ch == '<')
      a = a * 1LL * seed % MOD;
    else
      a = a * 1LL * inv_seed % MOD;
    auto it = map.find((a * 1LL * c % MOD + b) % MOD);
    if (it != map.end())
      ret += it->second;
    map[b] ++;
  }
  return ret;
}

