long long base=9901;
long long mo=1000000000000000031ll;

long long Mul(long long a,long long b){
	return (__int128)a*b%mo;
}
long long Add(long long a,long long b){
	return (a+b)%mo;
}
long long Pow(__int128 a,long long b){
	__int128 res=1;
	for(;b;b>>=1,a=a*a%mo)
		if(b&1)
			res=res*a%mo;
	return res;
}
vector<pair<long long,long long>> v;
map<long long,int> mp;
int main(){
	static char s[250000+10];
	int n;
	scanf("%d%s",&n,s);
	long long inv=Pow(base,mo-2);
	long long pos=1,val=0;
	v.push_back(make_pair(val,pos));
	for(int i=0;i<n;++i){
		if(s[i]=='<'){
			pos=Mul(pos,inv);
		}else if(s[i]=='>'){
			pos=Mul(pos,base);
		}else if(s[i]=='+'){
			val=Add(val,pos);
		}else if(s[i]=='-'){
			val=Add(val,mo-pos);
		}
		v.push_back(make_pair(val,pos));
	}
	long long V=v.back().first;
	long long ans=0;
	for(int i=v.size()-1;i>=0;--i){
		auto it=mp.find(Add(v[i].first,Mul(V,v[i].second)));
		if(it!=mp.end()){
			ans+=it->second;
		}
		++mp[v[i].first];
	}
	write(ans),putchar('\n');
	return 0;
}

