const int N = 250010, MOD = 998244353, BAS = 233;
inline void Add(int& x,int y) {
  x = x + y >= MOD ? x + y - MOD : x + y;
}
inline void Sub(int& x,int y) {
  x = x - y < 0 ? x - y + MOD : x - y;
}
int power(int a,int b) {
  int ret = 1;
  while (b) {
    if (b&1) ret = 1ll * ret * a % MOD;
    a = 1ll * a * a % MOD;
    b >>= 1;
  }
  return ret;
}

char str[N];
int n, pw[N << 1], ipw[N << 1], rec, val[2][N], tmp[2][N];
ll ans;
map<int,int> mp;
void solve(int l,int r) {
  if (l == r) {
    if (val[1][l] == rec)
      ++ ans;
    return;
  }
  int mid = (l + r) >> 1;
  mp.clear();
  tmp[0][mid] = val[0][mid], tmp[1][mid] = val[1][mid];
  rrp (i, mid-1, l) {
    tmp[0][i] = 1ll * val[0][i] * tmp[0][i+1] % MOD;
    tmp[1][i] = (1ll * val[0][i] * tmp[1][i+1] + val[1][i]) % MOD;
  }
  tmp[0][mid+1] = val[0][mid+1], tmp[1][mid+1] = val[1][mid+1];
  rep (i, mid+2, r) {
    tmp[0][i] = 1ll * tmp[0][i-1] * val[0][i] % MOD;
    tmp[1][i] = (1ll * tmp[0][i-1] * val[1][i] + tmp[1][i-1]) % MOD;
  }
  rep (i, mid+1, r) ++ mp[tmp[1][i]];
  rep (i, l, mid) {
    // tmp[0][i] * v + tmp[1][i] = rec
    int v = rec;
    Sub(v, tmp[1][i]);
    v = 1ll * v * power(tmp[0][i], MOD - 2) % MOD;
    ans += mp[v];
  }
  solve(l, mid);
  solve(mid+1, r);
}
int main() {
  read(n);
  scanf("%s", str+1);
  pw[0] = ipw[0] = 1;
  rep (i, 1, n * 2) pw[i] = 1ll * pw[i-1] * BAS % MOD;
  ipw[1] = power(BAS, MOD - 2);
  rep (i, 2, n*2) ipw[i] = 1ll * ipw[i-1] * ipw[1] % MOD;
  rep (i, 1, n) {
    if (str[i] == '+') val[0][i] = 1, val[1][i] = 1;
    if (str[i] == '-') val[0][i] = 1, val[1][i] = MOD - 1;
    if (str[i] == '<') val[0][i] = pw[1];
    if (str[i] == '>') val[0][i] = ipw[1];
  }
  rrp (i, n, 1)
    rec = (1ll * rec * val[0][i] + val[1][i]) % MOD;
  solve(1, n);
  cout << ans << endl;
  return 0;
}

