ll powMod(ll n, ll p, ll mod) {
	if (p < 0) p += mod - 1;
	n %= mod;
	ll res = 1;
	while (p) {
		if (p & 1) (res *= n) %= mod;
		(n *= n) %= mod;
		p >>= 1;
	}
	return res;
