	if (val > 0) {
		reverse(all(x));
		while (val--) {
			x.pb(0);
		}
		reverse(all(x));
	} else {
		reverse(all(x));
		val *= -1;
		while (val--) {
			x.pop_back();
		}
		reverse(all(x));
	}
