char S[N];
long long ans;
map<pair<int,int>,int>mp;
inline int pls(int x,int y,int M){
	return ((x+y)%M+M)%M;
}
inline void sol(bool fl){
	now=n,w1=w2=0;
	for(int i=1;i<=n;i++){
		//cerr<<i<<"===now:"<<now<<endl;
		if(fl)mp[{pls(1ll*b1[now]*a1%M1,1ll*w1*b1[n]%M1,M1),
		pls(1ll*b2[now]*a2%M2,1ll*w2*b2[n]%M2,M2)}]++;
		//cerr<<"ins "<<pls(1ll*b1[now]*a1%M1,1ll*w1*b1[n]%M1,M1)<<','<<
		//pls(1ll*b2[now]*a2%M2,1ll*w2*b2[n]%M2,M2)<<endl;
		if(S[i]>59)now+=S[i]-61;
		else{
			//cerr<<S[i]<<"?";
			tp=44-S[i];
			w1=pls(w1,tp*b1[now],M1);
			w2=pls(w2,tp*b2[now],M2);
		}
		if(fl)ans+=mp[{1ll*w1*b1[n]%M1,1ll*w2*b2[n]%M2}];
		//cerr<<"("<<w1<<','<<w2<<")\n";
	}
}
