#define ll long long
#define mid (l+r)/2
using namespace std;
const ll MOD=1e9+9,inv2=5e8+5;
int n;
char s[250005];
ll v,v1[250005],p1[250005],v2[250005],ans;
map<ll,int>mp;
void solve(int l,int r)
{
	if(l==r)
	{
		if(s[l]=='+'&&v==1)ans++;
		if(s[l]=='-'&&v==MOD-1)ans++;
		if(s[l]=='<'&&v==0)ans++;
		if(s[l]=='>'&&v==0)ans++;
		return;
	}
	solve(l,mid);
	solve(mid+1,r);
	v1[mid+1]=v2[mid]=0;
	p1[mid+1]=1;
	for(int i=mid;i>=l;i--)
	{
		if(s[i]=='+')v1[i]=(v1[i+1]+1)%MOD,p1[i]=p1[i+1];
		if(s[i]=='-')v1[i]=(v1[i+1]-1+MOD)%MOD,p1[i]=p1[i+1];
		if(s[i]=='<')v1[i]=v1[i+1]*inv2%MOD,p1[i]=p1[i+1]*2%MOD;
		if(s[i]=='>')v1[i]=v1[i+1]*2%MOD,p1[i]=p1[i+1]*inv2%MOD;
	}
	ll now=1;
	for(int i=mid+1;i<=r;i++)
	{
		if(s[i]=='+')v2[i]=(v2[i-1]+now)%MOD;
		if(s[i]=='-')v2[i]=(v2[i-1]-now+MOD)%MOD;
		if(s[i]=='<')now=now*inv2%MOD,v2[i]=v2[i-1];
		if(s[i]=='>')now=now*2%MOD,v2[i]=v2[i-1];
		mp[v2[i]]++;
	}
	for(int i=l;i<=mid;i++)ans+=mp[(v-v1[i]+MOD)*p1[i]%MOD];
	for(int i=mid+1;i<=r;i++)mp[v2[i]]=0;
}
int main()
{
	scanf("%d",&n);
	scanf("%s",s+1);
	for(int i=n;i>=1;i--)
	{
		if(s[i]=='+')v=(v+1)%MOD;
		if(s[i]=='-')v=(v-1+MOD)%MOD;
		if(s[i]=='<')v=v*inv2%MOD;
		if(s[i]=='>')v=v*2%MOD;
	}
	solve(1,n);
	printf("%lld\n",ans);
	return 0;
}

