using namespace std;

const int MOD = 999999937; 
const int MAXN = 250005;
const int K = 6;

char s[MAXN];

int n, X[MAXN][K], IX[MAXN][K], C[K], pos[MAXN];

vector<int> a[MAXN], b[MAXN];

map<vector<int>, int> mp;

char rev(char x)
{
	if (x == '<') return '>';
	if (x == '>') return '<';
	if (x == '+') return '-';
	if (x == '-') return '+';	
}

int qpow(int a, int b)
{
	int ret = 1;
	while (b)
	{
		if (b & 1) ret = (long long)ret * a % MOD;
		a = (long long)a * a % MOD;
		b >>= 1;	
	}
	return ret;
}

void T(int &c, char x, int k)
{
	if (x == '<') c = (long long)c * X[1][k] % MOD;
	if (x == '>') c = (long long)c * IX[1][k] % MOD;
	if (x == '+') c = (c + 1) % MOD;
	if (x == '-') c = (c - 1 + MOD) % MOD;
}

void init()
{
	for (int i = 0; i < K; i++)
		X[0][i] = 1;
	for (int i = 0; i < K; i++)
		X[1][i] = rand() % MOD; // ***************
	for (int i = 2; i < n; i++)
		for (int j = 0; j < K; j++)
			X[i][j] = (long long)X[i - 1][j] * X[1][j] % MOD;
	for (int i = 0; i < n; i++)
		for (int j = 0; j < K; j++)
			IX[i][j] = qpow(X[i][j], MOD - 2);
	for (int i = n; i >= 1; i--)
		for (int j = 0; j < K; j++)
			T(C[j], s[i], j);
//	cout << "C: " << C[0] << endl;
	for (int i = 0; i <= n + 1; i++)
		a[i].resize(K, 0), b[i].resize(K, 0);
}

int main()
{
	srand(2333);
	ios::sync_with_stdio(false);
	cin >> n;
	for (int i = 1; i <= n; i++)
		cin >> s[i];
	init();
	pos[n + 1] = 0;
	for (int i = n; i >= 1; i--)
	{
		for (int j = 0; j < K; j++)
			b[i][j] = b[i + 1][j];
		pos[i] = pos[i + 1];
		switch (rev(s[i])) 
		{
			case '<':
				pos[i]++;
				break;
			case '>':
				pos[i]--;
				break;
			case '+':
				if (pos[i] >= 0)
				{
					for (int j = 0; j < K; j++)
						b[i][j] = ((long long)b[i][j] + X[pos[i]][j]) % MOD;
				}
				else
				{
					for (int j = 0; j < K; j++)
						b[i][j] = ((long long)b[i][j] + IX[-pos[i]][j]) % MOD;
				}
				break;
			case '-':
				if (pos[i] >= 0)
				{
					for (int j = 0; j < K; j++)
						b[i][j] = ((long long)b[i][j] - X[pos[i]][j] + MOD) % MOD;
				}
				else
				{
					for (int j = 0; j < K; j++)
						b[i][j] = ((long long)b[i][j] - IX[-pos[i]][j] + MOD) % MOD;
				}
				break;	
		}
//		cout << "b[" << i << "]: " << b[i][0] << endl;
	}
	for (int i = n; i > 0; i--)
	{
		for (int j = 0; j < K; j++)
		{
			if (pos[i] >= 0)
				a[i][j] = ((long long)C[j] * X[pos[i]][j] % MOD + b[i][j]) % MOD;
			else
				a[i][j] = ((long long)C[j] * IX[-pos[i]][j] % MOD + b[i][j]) % MOD;
		}
//		cout << "b[" << i << "]: " << b[i][0] << endl;
		mp[a[i]]++;
	}
	long long res = 0;
	for (int i = n; i > 0; i--)
	{
		res += mp[b[i + 1]];
		mp[a[i]]--;
	}
	cout << res << endl;
	return 0;	
}

