#include<bits/stdc++.h>
#define Rint register int
using namespace std;
typedef long long LL;
typedef __int128 I128;
const int N = 250003;
const LL mod = 1000000000000000031ll, X = 1000000000000000003ll, inv = 821428571428571454ll;
int n, a[N];
LL ans, p[N], val[N];
char str[N];
map<LL, int> tong;
inline void upd(LL &a, LL b){a += b; if(a >= mod) a -= mod;}
inline LL add(LL a, LL b){return (a + b >= mod) ? (a + b - mod) : (a + b);}
int main(){
	scanf("%d%s", &n, str + 1);
	p[0] = 1;
	for(Rint i = 1;i <= n;i ++){
		p[i] = p[i - 1]; val[i] = val[i - 1];
		if(str[i] == '+') upd(val[i], p[i]);
		else if(str[i] == '-') upd(val[i], mod - p[i]);
		else if(str[i] == '<') p[i] = (I128) p[i] * inv % mod;
		else if(str[i] == '>') p[i] = (I128) p[i] * X % mod;
	}
	for(Rint i = n;i;i --){
		++ tong[val[i]];
		ans += tong[add((I128) p[i - 1] * val[n] % mod, val[i - 1])];
	}
	printf("%lld", ans);
}

#include <iostream>
#include <cstdio>
#include <map>
#define MN 250005

const long long mod = 213668850274623167;
const long long base = 257;
char s[MN];
long long c[MN];
long long fac[MN], inv[MN];
std::map<long long, int> mp;

long long mul(long long a, long long b)
{
	a = (a + mod) % mod;
	b = (b + mod) % mod;
	return (a * b - (long long)((long double) a / mod * b) * mod + mod) % mod;
}

long long qpow(long long x, long long p)
{
	long long ans = 1;
	while(p)
	{
		if(p & 1) ans = mul(ans, x);
		x = mul(x, x); p >>= 1;
	}
	return ans;
}

void add(int x, int o, long long &now)
{
	long long u;
	if(x < 0) u = inv[-x];
	else u = fac[x];
	now = (mul(u, o) + now) % mod;
}

int main()
{
	int n; scanf("%d", &n);
	scanf("%s", s);
	long long p = 0, now = 0;
	inv[1] = qpow(base, mod - 2);
	fac[0] = 1; fac[1] = base;
	for(int i = 2; i <= n; i++) inv[i] = mul(inv[i - 1], inv[1]), fac[i] = mul(fac[i - 1], fac[1]);
	for(int i = 0; i < n; i++)
	{
		if(s[i] == '<') p--;
		if(s[i] == '>') p++;
		if(s[i] == '+') add(p, 1, now);
		if(s[i] == '-') add(p, -1, now);
	}
	long long e = now; p = 0; now = 0;
	mp[e]++;
	long long ans = 0;
	for(int i = 0; i < n; i++)
	{
		if(s[i] == '<') p--;
		if(s[i] == '>') p++;
		if(s[i] == '+') add(p, 1, now);
		if(s[i] == '-') add(p, -1, now);
		long long u;
		if(p < 0) u = inv[-p];
		else u = fac[p];
		ans += mp[now];
		mp[(mul(e, u) + now ) % mod]++;
	}
	printf("%lld\n", ans);
	return 0;
}

