			case '+' : res += p3; res >= P ? res -= P : 0; break;
			case '-' : res -= p3; res < 0 ? res += P : 0; break;
			case '>' : res *= p2; ++cnt; res %= P; break;
			case '<' : res *= invp2; --cnt; res %= P; break;
		}
