int A[Maxn], B[Maxn];
map <int, int> M;
ll res;

int toPower(int a, int p)
{
	int res = 1;
	while (p) {
		if (p & 1) res = ll(res) * a % mod;
		p >>= 1; a = ll(a) * a % mod;
	}
	return res;
}

int Inv(int a) { return toPower(a, mod - 2); }

int main()
{
	iarg1 = Inv(arg1);
	scanf("%d", &n);
	scanf("%s", S + 1);
	A[0] = 1; B[0] = 0;
	for (int i = 1; i <= n; i++) {
		if (S[i] == '+') { A[i] = 1; B[i] = 1; }
		else if (S[i] == '-') { A[i] = 1; B[i] = mod - 1; }
		else if (S[i] == '>') { A[i] = arg1; B[i] = 0; }
		else if (S[i] == '<') { A[i] = iarg1; B[i] = 0; }
		B[i] = (ll(B[i - 1]) * A[i] + ll(B[i])) % mod;
		A[i] = ll(A[i - 1]) * A[i] % mod;
	}
	int X = ll(B[n]) * Inv(A[n]) % mod;
	M[X]++;
	for (int i = 1; i <= n; i++) {
		int ainv = Inv(A[i]);
		int need = ll(B[i]) * ainv % mod;
		auto it = M.find(need);
		if (it != M.end()) res += it->second;
		M[ll(X + B[i]) * ainv % mod]++;
	}
