inline int FUCK(int x, int y, int mod) {
    int res = 1;
    for (; y; y >>= 1, x = (LL)x * x % mod) if (y & 1) res = (LL)res * x % mod;
    return res;
}

struct Data {
    int val[2];

    Data(int a = 0, int b = 0) {val[0] = a, val[1] = b;} 

    Data operator + (const Data &b) {
        Data res;
        res.val[0] = (val[0] + b.val[0]) % mod[0];
        res.val[1] = (val[1] + b.val[1]) % mod[1];
        return res;
    }

    Data operator - (const Data &b) {
        Data res;
        res.val[0] = (val[0] - b.val[0] + mod[0]) % mod[0];
        res.val[1] = (val[1] - b.val[1] + mod[1]) % mod[1]; 
        return res;
    }

    Data operator * (const int b) {
        Data res = *this;
        int a = b >= 0 ? Pow[0][b] : FUCK(P[0], (LL)(mod[0] - 2) * (-b) % mod[0], mod[0]);
        int c = b >= 0 ? Pow[1][b] : FUCK(P[1], (LL)(mod[1] - 2) * (-b) % mod[1], mod[1]);
        res.val[0] = (LL)res.val[0] * a % mod[0];
        res.val[1] = (LL)res.val[1] * c % mod[1];
        return res;
    }

    bool operator < (const Data &b) const {
        if (val[0] != b.val[0]) return val[0] < b.val[0];
        return val[1] < b.val[1];
    }
};

map<Data, int> mp;

inline void increase(Data &a, int b) {
    a = a + (Data){Pow[0][b], Pow[1][b]};
}

inline void decrease(Data &a, int b) {
    a = a - (Data){Pow[0][b], Pow[1][b]};
}

