  Set() : k{1}, b{0} {}

  void add(int c) {
    if (c == '+') {
      b.add<-1>();
    } else if (c == '-') {
      b.add<1>();
    } else if (c == '>') {
      b.multiplyB();
      k.divideB();
    } else {
      k.multiplyB();
      b.divideB();
    }
  }

  Hash zero() const { return Hash{k, b}; }
  Hash inverse(const Hash &kb) const { return Hash{k, b, kb}; }

  Hash k, b;
};

