		if(s[i] == '<') --p[i];
		if(s[i] == '>') ++p[i];
		if(s[i] == '+') (prefix[i] += p_pow[p[i] + n]) %= MOD;
		if(s[i] == '-') (prefix[i] -= p_pow[p[i] + n]) %= MOD;
	}

	static unordered_map<int, int> cnt;

	LL ans = 0;
	for(int i = 1; i <= n; ++i) ans += prefix[i] == prefix[n];
	for(int i = 1; i <= n; ++i)
	{
		ans += cnt[(prefix[i] + MOD) % MOD];
		int res = (prefix[i] + (LL) p_pow[p[i] + n] * prefix[n] % MOD) % MOD;
		++cnt[(res + MOD) % MOD];
	}
	printf("%lld\n", ans);
}

