struct rolling_hash{
    ll a, b;
    rolling_hash() {
        a = b = 0;
    }
    rolling_hash(ll x) {
        a = x % P1;
        b = x % P2;
    }
    rolling_hash(ll x, ll y) {
        a = x % P1;
        b = y % P2;
    }
    rolling_hash operator + (const rolling_hash& A) const {
        return rolling_hash(a + A.a, b + A.b);
    }
    rolling_hash operator - (const rolling_hash& A) const {
        return rolling_hash(a - A.a + P1, b - A.b + P2);
    }
    rolling_hash operator * (const rolling_hash& A) const {
        return rolling_hash(a * A.a, b * A.b);
    }
    ll get() {
        return a * P2 + b;
    }
