    inv_base_pow[i] = (lint)inv_base * inv_base_pow[i - 1] % mod;

  vector<int> hash_diff(n, 0);
  vector<int> pos_before(n);
  int pos = 0;
  for (int i = 0; i < n; ++i) {
    pos_before[i] = pos;
    if (seq[i] == '>') ++pos;
    else if (seq[i] == '<') --pos;
    else {
      int base_pow_pos;
      if (pos >= 0) base_pow_pos = base_pow[pos];
      else base_pow_pos = inv_base_pow[-pos];
      if (seq[i] == '+') hash_diff[i] = base_pow_pos;
      else if (seq[i] == '-') hash_diff[i] = mod - base_pow_pos;
      else assert(0);
    }
  }
  int full_seq_hash = 0;
  for (int i = 0; i < n; ++i) {
    full_seq_hash += hash_diff[i];
    if (full_seq_hash >= mod) full_seq_hash -= mod;
  }

  vector<int> suffix(n + 1);
  suffix[n] = 0;
  for (int i = n - 1; i >= 0; --i) {
    suffix[i] = hash_diff[i] + suffix[i + 1];
    if (suffix[i] >= mod) suffix[i] -= mod;
  }

  lint result = 0;
  map<int, int> count;
  count[suffix[n]] += 1;
  for (int i = n - 1; i >= 0; --i) {
    int shifted_full;
    if (pos_before[i] >= 0)
      shifted_full = (lint)full_seq_hash * base_pow[pos_before[i]] % mod;
    else
      shifted_full = (lint)full_seq_hash * inv_base_pow[-pos_before[i]] % mod;

    int target = suffix[i] - shifted_full;
    if (target < 0) target += mod;

    auto it = count.find(target);
    if (it != count.end())
      result += it->second;

    count[suffix[i]] += 1;
  }
  printf("%lld\n", result);

  return 0;
}

