inline int fpow(int x,int y,int mod){
	if(y==0)return 1;
	long long res=fpow(x,y/2,mod);
	(res*=res)%=mod;
	return y%2==0?int(res):int(res*x%mod);
}

static const int Mult=4;
static const int Mod=1000000007;
static const int Advance[4]={113,113113,233,23333333};
static const int Offset=250005;

struct Hash{
	int a[4];
	static int pow[4][500010];

	Hash(){
		for(int i=0;i<Mult;i++)a[i]=0;
	}

	bool operator==(const Hash& r)const{
		for(int i=0;i<Mult;i++)if(a[i]!=r.a[i])return false;
		return true;
	}

	bool operator<(const Hash& r)const{
		for(int i=0;i<Mult;i++)if(a[i]!=r.a[i])return a[i]<r.a[i];
		return false;
	}

	Hash operator-(const Hash& r)const{
		Hash res;
		for(int i=0;i<Mult;i++)res.a[i]=(a[i]+Mod-r.a[i])%Mod;
		return res;
	}

	void inc(int pos,int x){
		x=(x+Mod)%Mod;
		for(int i=0;i<Mult;i++){
			(a[i]+=int((long long)pow[i][pos+Offset]*x%Mod))%=Mod;
			assert(a[i]>=0);
		}
	}

	void shift(int x){
		for(int i=0;i<Mult;i++){
			a[i]=int((long long)a[i]*pow[i][x+Offset]%Mod);
			assert(a[i]>=0);
		}
	}

	static void init(){
		for(int i=0;i<Mult;i++){
			int inv=fpow(Advance[i],Mod-2,Mod);
			pow[i][Offset]=1;
			for(int j=Offset-1;j>=0;j--)pow[i][j]=int((long long)pow[i][j+1]*inv%Mod);
			for(int j=Offset+1;j<500010;j++)pow[i][j]=int((long long)pow[i][j-1]*Advance[i]%Mod);
			for(int j=0;j<500010;j++)assert(pow[i][j]>=0);
		}
	}
};
