long long solve(int l,int r){
	if(l==r){
		Hash curr;
		if(s[l]=='+')curr.inc(0,1);
		else if(s[l]=='-')curr.inc(0,-1);
		return curr==all?1:0;
	}

	long long res=0;
	int mid=(l+r)/2;

	Hash right;
	int p=0;
	map<Hash,int> cnt;
	for(int i=mid+1;i<=r;i++){
		if(s[i]=='+')right.inc(p,1);
		else if(s[i]=='-')right.inc(p,-1);
		else if(s[i]=='<')p--;
		else if(s[i]=='>')p++;
		cnt[right]++;
	}

	Hash left;
	p=0;
	for(int i=mid;i>=l;i--){
		if(s[i]=='+')left.inc(0,1);
		else if(s[i]=='-')left.inc(0,-1);
		else if(s[i]=='<'){
			left.shift(-1);
			p--;
		}
		else if(s[i]=='>'){
			left.shift(1);
			p++;
		}
		Hash curr=all-left;
		curr.shift(-p);
		res+=cnt[curr];
	}

//	printf("%d [%d] %d => %lld\n",l,mid,r,res);

	res+=solve(l,mid);
	res+=solve(mid+1,r);
	return res;
}

