typedef unsigned long long ll;typedef vector<int> vi;typedef pair<int,int> pi;const int inf = (int)1e9;const double INF = 1e12, EPS = 1e-9;

ll mod, modInv;
ll pw(ll n, ll m){
	ll res = 1;
	for(; m; m /= 2){
		if(m & 1) res = res * n;
		n = n * n;
	}
	return res;
}
ll h_[510000], *h = h_ + 255000;

void init(int n, ll mod_){
	mod = mod_;
	modInv = pw(mod, -1);
	h[0] = 1;
	ll x = 1, y = 1;
	for(int i = 1; i <= n; i++){
		x *= mod;
		y *= modInv;
		h[i] = x;
		h[-i] = y;
	}
}

ll solve(int n, const string &s, ll mod){
	init(n, mod);
	int p = 0;
	ll hash = 0;
	for(char c : s){
		if(c == '<') p--;
		if(c == '>') p++;
		if(c == '+') hash += h[p];
		if(c == '-') hash -= h[p];
	}

	int p2 = p;
	ll h2 = 0;
	map<ll, vi> pos;
	for(int i = n; i >= 0; i--){
		if(i < n){
			char c = s[i];
			if(c == '<') p2++;
			if(c == '>') p2--;
			if(c == '+') h2 -= h[p2];
			if(c == '-') h2 += h[p2];
		}
		pos[h2].pb(i);
	}
	for(auto &i: pos) reverse(all(i.second));

	int p3 = p;
	ll h3 = hash, ans = 0;
	rep(i, n){
		ll diff = (hash - h3) * h[p - p3];
		if(pos.count(diff)) ans += pos[diff].end() - upper_bound(all(pos[diff]), i);

		char c = s[i];
		if(c == '<') p3++, h3 *= mod;
		if(c == '>') p3--, h3 *= modInv;
		if(c == '+') h3--;
		if(c == '-') h3++;
	}
	return ans;
}

int main(){
	cin.tie(0); cin.sync_with_stdio(0);

	int n; string s; cin >> n >> s;
	map<ll,int> ans;
	vector<ll> mods;
	for(ll p = 1e9 + 1; mods.size() < 5; p += 2){
		for(ll i = 3; i * i <= p; i += 2) if(p % i == 0) goto NG;
		mods.pb(p); NG:;
	}
	for(ll mod : mods) ++ans[solve(n, s, mod)];

	int mxc = 0; ll res = 0;
	for(auto i : ans) if(i.second > mxc) mxc = i.second, res = i.first;
	cout << res << endl;

	return 0;
}

