long long ruijo(long long  k,long long m, ll mod){
	if(m==0)return 1;
	if(m==1)return k%mod;
	long long s = ruijo(k,m/2,mod);
	if(m%2==0){
		return (s*s)%mod;
	}else{
		long long ans;
		ans = (s*s)%mod;
		return (k*ans)%mod;
	}
}


