typedef pair<ll, ll> PL;


ll powmod(ll a, ll e, ll mod) {
  ll sum = 1;
  ll cur = a;
  while (e > 0) {
    if (e % 2) {
      sum = sum * cur % mod;
    }
    cur = cur * cur % mod;
    e /= 2;
  }
  return sum;
}



int main(void) {
  const ll mod = 1e9 + 7;
  const ll mod2 = 1e9 + 9;
  ios::sync_with_stdio(false);
  cin.tie(0);
  int n;
  string s;
  cin >> n >> s;
  int p = 0;
  VI pos(n + 1);
  mt19937 mt;
  VL b(3);
  ll mods[3] = { mod, mod2, 998244353 };
  REP(c, 0, 3) {
    do {
      b[c] = mt() % mods[c];
    } while (b[c] == 0);
  }
  vector<VL> hsh(3, VL(n + 1));
  ll inv[3] = { 0 };
  REP(c, 0, 3) inv[c] = powmod(b[c], mods[c] - 2, mods[c]);
  REP(c, 0, 3) {
    p = 0;
    ll cur = 1;
    REP(i, 0, n) {
      ll tmp = hsh[c][i];
      if (s[i] == '>') {
        p++;
        cur = cur * b[c] % mods[c];
      }
      if (s[i] == '<') {
        p--;
        cur = cur * inv[c] % mods[c];
      }
      if (s[i] == '+') {
        tmp = (tmp + cur) % mods[c];
      }
      if (s[i] == '-') {
        tmp = (tmp + mods[c] - cur) % mods[c];
      }
      hsh[c][i + 1] = tmp;
      pos[i + 1] = p;
    }
  }
  if (DEBUG) {
    REP(i, 0, n + 1) {
      cerr << i << " " << hsh[0][i] << endl;
    }
  }
  VL meguru(3);
  REP(c, 0, 3) meguru[c] = hsh[c][n];
  map<VL, int> kirika;
  kirika[meguru] = 1;
  ll tot = 0;
  VL cur(3);
  REP(c, 0, 3) cur[c] = powmod(b[c], pos[n], mods[c]);
  for (int i = n - 1; i >= 0; --i) {
    if (DEBUG) {
      DEBUGP(i);
      DEBUGP(pos[i]);
    }
    if (pos[i + 1] - pos[i] == 1) {
      REP(c, 0, 3) cur[c] = cur[c] * inv[c] % mods[c];
    }
    else if (pos[i + 1] - pos[i] == -1) {
      REP(c, 0, 3) cur[c] = cur[c] * b[c] % mods[c];
    }
    VL ken(3);
    REP(c, 0, 3) ken[c] = cur[c] * meguru[c] + hsh[c][i] % mods[c];
    tot += kirika[ken];
    VL tt(3);
    REP(c, 0, 3) tt[c] = hsh[c][i];
    kirika[tt] += 1;
  }
  cout << tot << endl;
}

