ll powMod(ll n, ll p, ll mod) {
	if (p < 0) p += mod - 1;
	n %= mod;
	ll res = 1;
	while (p) {
		if (p & 1) (res *= n) %= mod;
		(n *= n) %= mod;
		p >>= 1;
	}
	return res;
}

constexpr int N = 3;
constexpr int M[N] = { 1000000009, 1000000021, 1000000033 };
constexpr int B = 123457;

struct Hash {
	int val[N];
	Hash& norm() {
		REP(i, N) if ((val[i] %= M[i]) < 0) val[i] += M[i];
		return *this;
	}

	Hash(int x) { REP(i, N) val[i] = x; norm(); }
	Hash() : Hash(0) {}

	static Hash makePow(int n, int p) {
		Hash res;
		REP(i, N) res.val[i] = powMod(n, p, M[i]);
		return res;
	}
	static Hash makeInv(int x) {
		Hash res;
		REP(i, N) res.val[i] = powMod(x, M[i] - 2, M[i]);
		return res;
	}

	bool operator==(const Hash & r) const { return *this == r; }
	bool operator<(const Hash & r) const { 
		REP(i, N) if(this->val[i] != r.val[i]) return this->val[i] < r.val[i];
		return false;
	}
	Hash& operator+=(const Hash & r) { REP(i, N) val[i] += r.val[i]; norm(); return *this; }
	Hash& operator-=(const Hash & r) { REP(i, N) val[i] -= r.val[i]; norm(); return *this; }
	Hash& operator*=(const Hash & r) { REP(i, N) val[i] *= r.val[i]; norm(); return *this; }
	Hash& operator/=(const Hash & r) { 
		REP(i, N) val[i] *= powMod(r.val[i], M[i] - 2, M[i]); 
		norm(); 
		return *this; 
	}
};

