const Int N = 300000;

ll mod_pow(ll x,ll n,Int mod){
  ll res=1;
  while(n){
    if(n%2) res=res*x%mod;
    x=x*x%mod;
    n/=2;
  }
  return res;
}

ll mod_inv(Int num,Int mod){return mod_pow(num, mod-2, mod);}


class Hash{
public:
  ll B, inv, mod;
  ll num;
  ll idx;
  vector<ll> K;
  Hash(Int B,Int mod):B(B),mod(mod){
    K.resize(N);
    K[0] = 1;
    for(Int i=1;i<N;i++) K[i] = K[i-1] * B % mod;

    inv = mod_pow(B, mod-2, mod);
    num = 0;
    idx = K[N/2];
  }

  void inc(){num = (num + idx) % mod;}
  void dec(){num = (num - idx + mod) % mod;}
  void moveLeft(){idx = idx * inv % mod;}
  void moveRight(){idx = idx * B % mod;}

};


signed main(){
