  vector<Hash> hash({Hash(1777771, 1e9+7),Hash(10000019,1000000009),Hash(10000079,1000000021)});
  vector<Hash> hash2 = hash;

  for(Int k = 0; k<n ; k++){
    char ch = s[k];
    for(Int i=0;i<3;i++){
      auto &A = hash[i];
      if(ch == '+') A.inc();
      if(ch == '-') A.dec();
      if(ch == '<') A.moveLeft();
      if(ch == '>') A.moveRight();
      cnt[i][A.num]++;
    }
  }

  vector<ll> allHash(3);
  vector<ll> Ofset(3,0);
  for(Int i=0;i<3;i++) allHash[i] = hash[i].num;

  Int ans = 0;
  for(Int k=0;k<n;k++){

    char ch = s[k];
    Int mn = INF;
    for(Int i=0;i<3;i++){
      auto &A = hash2[i];
      auto &U = allHash[i];
