constexpr int ksm(int x,int k,int mod){
	int ret=1,tmp=x;
	while(k){
		if(k&1)ret=1ll*ret*tmp%mod;
		tmp=1ll*tmp*tmp%mod;k>>=1;
	}
	return ret;
}
template<int mod,int base,int invbase=ksm(base,mod-2,mod)>
struct integer{
	int val;
	integer(){val=0;}
	integer(int v){val=(v%mod+mod)%mod;}
	int operator * (const int rhs){return 1ll*val*rhs%mod;}
	int operator + (const int rhs){return (val+rhs)%mod;}
	int operator - (const int rhs){return ((val-rhs)%mod+mod)%mod;}
	operator int(){return val;}
	void rf(){val=1ll*val*base%mod;}
	void rb(){val=1ll*val*invbase%mod;}
};
integer<mod1,mod2,mod3>a;
template<class T>
struct node{
	T hash,cur;
	void init(){hash=0;cur=1;}
	void add(char c){
		if(c=='>')cur.rf();
		if(c=='<')cur.rb();
		if(c=='+')hash=hash+cur;
		if(c=='-')hash=hash-cur;
	}
	node operator*(node rhs){
		node result;
		result.cur=cur*rhs.cur;
		result.hash=rhs.hash*cur;
		result.hash=result.hash+hash;
		return result;
	}
	bool operator<(const node&rhs)const{
		return hash.val<rhs.hash.val;
	}
	bool operator!=(const node&rhs)const{
		return hash.val!=rhs.hash.val;
	}
};
template<class T,class...args>
struct state{
	node<T>a;
	state<args...>b;
