const int MOD = 1e9 + 7;

int inv(int a)
{
    assert(a);
    return a == 1 ? 1 : 1LL * (MOD - MOD / a) * inv(MOD % a) % MOD;
}

struct Mat
{
    Mat()
    {
        memset(a, 0, sizeof(a));
    }

    Mat inv() const
    {
        int det = (1LL * a[0][0] * a[1][1] + MOD - 1LL * a[0][1] * a[1][0] % MOD) % MOD;
        int inv_det = ::inv(det);
        Mat b;
        b[0][0] = 1LL * inv_det * a[1][1] % MOD;
        b[0][1] = 1LL * inv_det * (MOD - a[0][1]) % MOD;
        b[1][0] = 1LL * inv_det * (MOD - a[1][0]) % MOD;
        b[1][1] = 1LL * inv_det * a[0][0] % MOD;
        return b;
    };

    std::pair<int, int> get() const
    {
        return {a[0][0], a[1][0]};
    }

    int* operator[](int i) { return a[i]; }
    const int* operator[](int i) const { return a[i]; }

    int a[2][2];
};

Mat operator * (const Mat& a, const Mat& b)
{
    Mat c;
    for (int i = 0; i < 2; ++ i) {
        for (int j = 0; j < 2; ++ j) {
            c[i][j] = (1LL * a[i][0] * b[0][j] + 1LL * a[i][1] * b[1][j]) % MOD;
        }
    }
    return c;
}

