typedef long long D;
const int maxn=500005,mod1=1000000007,mod2=1000000009;
int qpow(int x,int y,int mod){
	int ans=1;
	while(y){
		if(y&1)ans=D(ans)*x%mod;
		x=D(x)*x%mod;
		y>>=1;
	}
	return ans;
}
int n;
