struct H{
	int a,b;
	H():a(0),b(0){}
	H(int _a,int _b):a(_a),b(_b){}
	H operator+(const H &x)const{return H((a+x.a)%mod1,(b+x.b)%mod2);}
	H operator-(const H &x)const{return H((a-x.a+mod1)%mod1,(b-x.b+mod2)%mod2);}
	H operator*(const H &x)const{return H((D)a*x.a%mod1,(D)b*x.b%mod2);}
	bool operator<(const H &x)const{return a!=x.a?a<x.a:b<x.b;}
}b,inv,f[maxn],g[maxn];
map<H,int> mp;
int main(){
	b=H(23,29),inv=H(qpow(23,mod1-2,mod1),qpow(29,mod2-2,mod2));
