ll m_pow(ll a,ll b,ll p){
	ll ret = 1;
	ll tmp = a;
	for(ll i = 0LL;i < 60;i++){
		if(b & (1LL << i)){
			ret *= tmp;
			ret %= p;
		}
		tmp *= tmp;
		tmp %= p;
	}
	return ret;
}

ll m_inv(ll a,ll p){
	return m_pow(a,p-2,p);
}

void get_f(int num,int type){
	pair<ll,ll> now = MP(1,0);
	pair<ll,ll> prev;
	if(num == n-1){
		prev = MP(1,0);
	}
	else{
		prev = f[type][num+1];
	}

	if(str[num] == '<'){
		now.FI = kake[type];
	}
	else if(str[num] == '>'){
		now.FI = m_inv(kake[type],mod[type]);
	}
	else if(str[num] == '+'){
		now.SE = mod[type] - 1;
	}
	else{
		now.SE++;
	}

	ll fir = now.FI * prev.FI % mod[type];
	ll sec = (now.SE * prev.FI % mod[type] + prev.SE) % mod[type];
	f[type][num] = MP(fir,sec);
}

pair<ll,ll> dfs(int num,int type){
	pair<ll,ll> now = MP(1,0);
	pair<ll,ll> prev;
	if(num == 0){
		prev = MP(1,0);
	}
	else{
		prev = dfs(num-1,type);
	}

	if(str[num] == '>'){
		now.FI = kake[type];
	}
	else if(str[num] == '<'){
		now.FI = m_inv(kake[type],mod[type]);
	}
	else if(str[num] == '-'){
		now.SE = mod[type] - 1;
	}
	else{
		now.SE++;
	}

	ll fir = now.FI * prev.FI % mod[type];
	ll sec = (now.SE * prev.FI % mod[type] + prev.SE) % mod[type];
	return MP(fir,sec);
}

ll get_x(ll x,ll num,ll type){
	return (f[type][num].FI * x % mod[type] + f[type][num].SE) % mod[type];
}

