			r=r*x%p;
	return r;
}
template<int x,int p>
	struct hsh{
		int v;
		hsh<x,p> operator<<(int n){return {v*qpow(x,p-1+n,p)%p};}
		hsh<x,p> operator>>(int n){return {v*qpow(x,p-1-n,p)%p};}
		hsh<x,p> operator+(hsh<x,p> o){return {(v+o.v+p)%p};}
		hsh<x,p> operator-(hsh<x,p> o){return {(v-o.v+p)%p};}
		hsh<x,p> operator+(int o){return {(v+o+p)%p};}
		hsh<x,p> operator-(int o){return {(v-o+p)%p};}
		bool operator==(hsh<x,p> o){return v==o.v;}
	};
typedef hsh<89,1000000007> H1;
