#include <bits/stdc++.h>
using namespace std;
const int M = 2;
struct hs {
	int v[M];
	hs(){
		memset(v, 0, sizeof(v));
	}
};
hs mods;
int mds[] = {
	961'748'927,
	961'820'429,
	1'000'000'007,
	1'000'000'009,
	1'003'162'753,
	1'023'893'771
};
hs create(int x){
	hs r;
	for(int i = 0; i < M; i++){
		r.v[i] = x % mods.v[i];
		if(r.v[i] < 0) r.v[i] += mods.v[i];
	}
	return r;
}
hs operator + (hs a, hs b){
	hs r;
	for(int i = 0; i < M; i++){
		r.v[i] = a.v[i] + b.v[i];
		if(r.v[i] >= mods.v[i]) r.v[i] -= mods.v[i];
	}
	return r;
}
hs operator - (hs a, hs b){
	hs r;
	for(int i = 0; i < M; i++){
		r.v[i] = a.v[i] - b.v[i];
		if(r.v[i] < 0) r.v[i] += mods.v[i];
	}
	return r;
}
hs operator * (hs a, hs b){
	hs r;
	for(int i = 0; i < M; i++){
		r.v[i] = 1ll * a.v[i] * b.v[i] % mods.v[i];
	}
	return r;
}
bool operator < (hs a, hs b){
	for(int i = 0; i < M; i++) if(a.v[i] != b.v[i]) return a.v[i] < b.v[i];
	return false;
}
int pwr(int a, int b, int mod){
	int r = 1;
	for(; b; b>>=1, a = 1ll * a * a % mod) if(b&1) r = 1ll * r * a % mod;
	return r;
}
bool is_prime(int x){
	for(int i = 2; i * i <= x; i++) if(x % i == 0) return false;
	return true;
}
const int maxn = 300300;
using Poly = pair<hs, hs>;
hs eval(Poly f, hs x){
	return f.first + (f.second * x);
}
Poly poly[maxn];
int main(){
	srand(time(NULL));
	memcpy(mods.v, mds, sizeof(int) * M);
	hs x, mx, minus = create(-1), plus = create(1);
