	for(int i = n - 1; i >= 0; i--){
		poly[i] = poly[i + 1];
		if(s[i] == '+'){
			poly[i].first = poly[i].first - poly[i].second;
		} else if(s[i] == '-'){
			poly[i].first = poly[i].first + poly[i].second;
		} else {
			hs mul = x;
			if(s[i] == '<')
				mul = mx;
			poly[i].second = poly[i].second * mul;
		}
	}
	Poly norm = poly[n];
	for(int i = 0; i < n; i++){
		if(s[i] == '+'){
			norm.first = norm.first + norm.second;
		} else if(s[i] == '-'){
			norm.first = norm.first - norm.second;
		} else if(s[i] == '<'){
			norm.second = norm.second * x;
		} else {
			norm.second = norm.second * mx;
		}
	}
	hs tot = norm.first;
	map<hs, int> cnt;
	cnt[poly[n].first]++;
	long long ans = 0;
	for(int i = n - 1; i >= 0; i--){
		ans += cnt[eval(poly[i], tot)];
		cnt[poly[i].first]++;
	}
	cout << ans << endl;
	return 0;
}

